mapper.update:
update rm_task_info t
    set t.HANDLE_PERSON=#{handlePerson},t.HANDLE_RESULTS=#{handleResults},t.HANDLE_TIME=#{handleTime}
where PROC_INST_ID=#{procInstId}

mapper.select:
select ta.TASK_NAME as taskName, ta.TASK_CONTENT as taskContent, ta.END_TIME as endTime, ve.VENUES_ADDRES as venuesAddres, case ta.EMERGENCY_LEVEL when '01' then '紧急' when '02' then '普通' else ta.EMERGENCY_LEVEL end as emergencyLevel
from rm_task_info ta left join rm_venues_info ve on ve.VENUES_ID=ta.REL_VENUES_ID left join act_ru_task ac on ta.PROC_INST_ID=ac.PROC_INST_ID_ where ac.assignee_=#{loginNm}
and ta.TASK_NAME LIKE CONCAT('%',#{taskName},'%')
and ta.TASK_CONTENT LIKE CONCAT('%',#{taskContent},'%')
order by ta.END_TIME desc limit #{page},#{size}

select ta.TASK_NAME as taskName, ta.TASK_CONTENT as taskContent, ta.END_TIME as endTime, ve.VENUES_ADDRES as venuesAddres, case ta.EMERGENCY_LEVEL when '01' then '紧急' when '02' then '普通' else ta.EMERGENCY_LEVEL end as emergencyLevel, ta.HANDLE_TIME as handleTime, ta.HANDLE_RESULTS as handleResults, ta.PROC_INST_ID as procInstId, ta.FLOW_TYPE as flowType, ta.TASK_TYPE as taskType
from rm_task_info ta left join rm_venues_info ve on ve.VENUES_ID=ta.REL_VENUES_ID where ta.PROC_INST_ID in
#{procInstId}
limit #{page},#{size}

select pro.NAME_ as name, pro.DESCRIPTION_ as description, pro.KEY_ as taskKey, pro.VERSION_ as version, pro.RESOURCE_NAME_ as resourceName from act_re_procdef pro where 1=1
and pro.NAME_ LIKE CONCAT('%',#{taskName},'%')
order by pro.KEY_ ,pro.VERSION_ desc limit #{page},#{size}
