mapper.update:
update rm_task_info t
    set t.HANDLE_PERSON=#{handlePerson},
    t.HANDLE_RESULTS=#{handleResults},
    t.HANDLE_TIME=#{handleTime}
where PROC_INST_ID=#{procInstId}

mapper.select:
SELECT ta.TASK_NAME as taskName, ve.VENUES_NAME as venuesAddres, di.DICT_CN_DESC as flowType, DATE_FORMAT(ta.END_TIME,'%Y-%m-%d %H:%i:%s') as endTime, case when ta.FLOW_TYPE in ('03','04','06') then u.MANAGER_CN_NM else us.USER_NM end as launchPerson, case ta.HANDLE_RESULTS when '0' then '未解决' when '1' then '已解决' else ta.HANDLE_RESULTS end as handleResults, case when ta.HANDLE_TIME is null then '进行中' else '已结束' end as statTask, ta.PROC_INST_ID as procInstId FROM rm_task_info ta left join rm_venues_info ve on ve.VENUES_ID=ta.REL_VENUES_ID left join rm_sys_dict di on ta.FLOW_TYPE=di.DICT_CD and di.DICT_TYPE_CD='1003' left join rm_sys_user_info us on us.USER_MOBILE=ta.LAUNCH_PERSON left join rm_venues_manager_info u on u.MANAGER_MOBILE=ta.LAUNCH_PERSON left join act_hi_identitylink ac on ac.PROC_INST_ID_ = ta.PROC_INST_ID and ac.TYPE_='starter' where 1=1
and ac.USER_ID_=#{vo.searchThree}
and ta.TASK_NAME LIKE CONCAT('%',#{vo.searchOne},'%')
and ve.VENUES_NAME LIKE CONCAT('%',#{vo.searchTwo},'%')
order by ta.HANDLE_RESULTS,ta.END_TIME limit #{vo.page},#{vo.size}

select
    sum(allSum) as allSum,sum(startSum) as startSum,sum(executeNum) as executeNum
from (
    (
        select
            count(ta.PROC_INST_ID)  as allSum,count(PROC_INST_ID) AS startSum, 0 AS executeNum
        from rm_task_info ta
        where 1=1

            and ta.LAUNCH_PERSON = #{loginNm}

    )union all(
        select
            count(co.PROC_INST_ID_) as allSum, 0 AS startSum  ,count(co.PROC_INST_ID_) AS executeNum
        from act_hi_comment co
        where 1=1

            and co.USER_ID_ = #{loginNm}

    )union all(
        select
            count(ac.PROC_INST_ID_) as allSum, 0 AS startSum   ,count(ac.PROC_INST_ID_) AS executeNum
        from act_hi_actinst ac
        where  ac.END_TIME_ is null

            and ac.ASSIGNEE_ = #{loginNm}

    )
) al

select ta.TASK_NAME as taskName, ta.TASK_CONTENT as taskContent, DATE_FORMAT(ta.END_TIME ,'%Y-%m-%d %H:%i:%s') as endTime, ve.VENUES_ADDRES as venuesAddres, case ta.EMERGENCY_LEVEL when '01' then '紧急' when '02' then '普通' else ta.EMERGENCY_LEVEL end as emergencyLevel, ta.EMERGENCY_LEVEL as emergencyLevelCode, case when FLOW_TYPE in ('06','04','03') then ma.MANAGER_CN_NM else us.USER_NM end as launchPerson, DATE_FORMAT(ta.LAUNCH_TIME ,'%Y-%m-%d %H:%i:%s') as launchTime, DATE_FORMAT(ta.HANDLE_TIME ,'%Y-%m-%d %H:%i:%s') as handleTime, ta.HANDLE_RESULTS as handleResults, ta.PROC_INST_ID as procInstId from rm_task_info ta left join rm_venues_info ve on ve.VENUES_ID=ta.REL_VENUES_ID left join rm_sys_user_info us on ta.LAUNCH_PERSON=us.USER_MOBILE left join rm_venues_manager_info ma on ma.MANAGER_MOBILE=ta.LAUNCH_PERSON where ta.PROC_INST_ID in
#{procInstId}
order by ta.END_TIME desc limit #{page},#{size}

select pro.NAME_ as name, pro.DESCRIPTION_ as description, pro.KEY_ as taskKey, pro.VERSION_ as version, pro.RESOURCE_NAME_ as resourceName from act_re_procdef pro where 1=1
and pro.NAME_ LIKE CONCAT('%',#{taskName},'%')
order by pro.KEY_ ,pro.VERSION_ desc limit #{page},#{size}

select
    loginNm
from
(
    (select
        f.USER_MOBILE AS loginNm,IDENTITY
    from rm_sys_user_info f
    where f.VALID_IND='1'
    and f.IDENTITY in

        #{identity}


        and f.province=#{province}


        and f.city=#{city}


        and f.area=#{area}


        and f.town=#{town}

)union all(
    select
        f.USER_MOBILE AS loginNm,IDENTITY
    from rm_sys_user_info f
    where f.VALID_IND='1'
    and f.IDENTITY in ('10000006','10000007')

        and f.REL_VENUES_ID LIKE CONCAT('%',#{relVenuesId},'%')

    )
) uu order by uu.IDENTITY

select t.FILING_ID as filingId, t.TASK_NAME as taskName, DATE_FORMAT(t.TASK_TIME,'%Y-%m-%d %H:%i:%s') as taskTime, v.VENUES_NAME as venuesName, t.PART_NUM as partNum, t.RESPONSIBLE_PERSON as responsiblePerson, t.TASK_CONTENT as taskContent from rm_filing_info t left join rm_venues_info v on v.VENUES_ID =t.REL_VENUES_ID left join rm_venues_manager_info m on v.RESPONSIBLE_PERSON=m.MANAGER_ID where t.FILING_STATUS='02' and m.MANAGER_MOBILE=#{login}
AND ( t.TASK_NAME LIKE CONCAT('%',#{search},'%') or t.TASK_CONTENT LIKE CONCAT('%',#{search},'%') or v.VENUES_NAME LIKE CONCAT('%',#{search},'%') )
order by t.TASK_TIME desc limit #{page},#{size}

SELECT tm.tmDate as date, IFNULL(comm.commTotal,0) as handleTotal, IFNULL(act.actTotal,0) as launchTotal FROM ( SELECT DATE_FORMAT(date_add(CURDATE(), INTERVAL i MONTH),'%Y-%m') AS tmDate FROM num a where a.i >#{num} and type='month' ) tm left join( select DATE_FORMAT(co.TIME_,'%Y-%m') as evDate, count(co.ID_) as commTotal from act_hi_comment co left join rm_task_info ta on co.PROC_INST_ID_=ta.PROC_INST_ID where DATE_FORMAT(co.TIME_,'%Y-%m-%d') > date_add(CURDATE(), interval 31*#{num} day) and ta.HANDLE_TIME is not null
and co.USER_ID_= #{login}
group by evDate ) comm on comm.evDate=tm.tmDate left join ( select DATE_FORMAT(ac.END_TIME_,'%Y-%m') as evDate, count(ac.ID_) as actTotal FROM act_hi_actinst ac left join rm_task_info ta on ac.PROC_INST_ID_=ta.PROC_INST_ID where DATE_FORMAT(ac.END_TIME_,'%Y-%m-%d') > date_add(CURDATE(), interval 31*#{num} day) and ac.ACT_TYPE_='startEvent' and ta.HANDLE_TIME is not null
and ac.ASSIGNEE_=#{login}
group by evDate )act on act.evDate=tm.tmDate ORDER BY tm.tmDate

SELECT DATE_FORMAT(tm.tmDate,'%Y-%m-%d') as date, IFNULL(comm.commTotal,0) as handleTotal, IFNULL(act.actTotal,0) as launchTotal FROM( SELECT date_add(CURDATE(), interval i day) AS tmDate FROM num a where a.i > #{num} and type='day' ) tm left join( select DATE_FORMAT(co.TIME_,'%Y-%m-%d') as evDate, count(co.ID_) as commTotal from act_hi_comment co left join rm_task_info ta on co.PROC_INST_ID_=ta.PROC_INST_ID where DATE_FORMAT(co.TIME_,'%Y-%m-%d') > date_add(CURDATE(), interval #{num} day) and ta.HANDLE_TIME is not null
and co.USER_ID_= #{login}
group by evDate ) comm on comm.evDate=tm.tmDate left join ( select DATE_FORMAT(ac.END_TIME_,'%Y-%m-%d') as evDate, count(ac.ID_) as actTotal FROM act_hi_actinst ac left join rm_task_info ta on ac.PROC_INST_ID_=ta.PROC_INST_ID where DATE_FORMAT(ac.END_TIME_,'%Y-%m-%d') > date_add(CURDATE(), interval #{num} day) and ac.ACT_TYPE_='startEvent' and ta.HANDLE_TIME is not null
and ac.ASSIGNEE_=#{login}
group by evDate )act on act.evDate=tm.tmDate ORDER BY tm.tmDate

SELECT (@rownum := @rownum + 1) AS date, IFNULL(comm.tots,0) as handleTotal, IFNULL(act.tots,0) as launchTotal FROM( SELECT i AS tmDate FROM num a where a.i > #{num} and type='day' ) tm LEFT JOIN ( SELECT ors.weeks, IFNULL(ors.tot,0) as tots from ( ( select WEEK(co.TIME_,3) -(WEEK(CURDATE(),3)) as weeks, count(co.ID_) as tot from act_hi_comment co left join rm_task_info ta on co.PROC_INST_ID_=ta.PROC_INST_ID where DATE_FORMAT(co.TIME_,'%Y')=DATE_FORMAT(CURDATE(),'%Y') and DATE_FORMAT(co.TIME_,'%Y-%m-%d') > date_add(CURDATE(), interval (#{num}+1)*7 day) and ta.HANDLE_TIME is not null
and co.USER_ID_= #{login}
GROUP BY weeks ORDER BY weeks )union all( select (WEEK(co.TIME_,3) -(select WEEK(num.REMARK,3) as h from num where type='lastYear') - (WEEK(CURDATE(),3))) as weeks, count(co.ID_) as tot from act_hi_comment co left join rm_task_info ta on co.PROC_INST_ID_=ta.PROC_INST_ID where DATE_FORMAT(co.TIME_,'%Y')=DATE_FORMAT(CURDATE(),'%Y')-1 and DATE_FORMAT(co.TIME_,'%Y-%m-%d') > (select date_add(subdate(curdate(),date_format(curdate(),'%w')-1),interval 7*(#{num}+1)-1 day)from dual) and ta.HANDLE_TIME is not null
and co.USER_ID_= #{login}
GROUP BY weeks ORDER BY weeks desc ) ) ors )comm on tm.tmDate=comm.weeks LEFT JOIN ( SELECT ors.weeks, IFNULL(ors.tot,0) as tots from ( ( select WEEK(ac.END_TIME_,3) -(WEEK(CURDATE(),3)) as weeks, count(ac.ID_) as tot from act_hi_actinst ac left join rm_task_info ta on ac.PROC_INST_ID_=ta.PROC_INST_ID where DATE_FORMAT(ac.END_TIME_,'%Y')=DATE_FORMAT(CURDATE(),'%Y') and DATE_FORMAT(ac.END_TIME_,'%Y-%m-%d') > date_add(CURDATE(), interval (#{num}+1)*7 day) and ac.ACT_TYPE_='startEvent' and ta.HANDLE_TIME is not null
and ac.ASSIGNEE_= #{login}
GROUP BY weeks ORDER BY weeks )union all( select (WEEK(ac.END_TIME_,3) -(select WEEK(num.REMARK,3) as h from num where type='lastYear') - (WEEK(CURDATE(),3))) as weeks, count(ac.ID_) as tot from act_hi_actinst ac left join rm_task_info ta on ac.PROC_INST_ID_=ta.PROC_INST_ID where DATE_FORMAT(ac.END_TIME_,'%Y')=DATE_FORMAT(CURDATE(),'%Y')-1 and DATE_FORMAT(ac.END_TIME_,'%Y-%m-%d') > (select date_add(subdate(curdate(),date_format(curdate(),'%w')-1),interval 7*(#{num}+1)-1 day)from dual) and ac.ACT_TYPE_='startEvent' and ta.HANDLE_TIME is not null
and ac.ASSIGNEE_= #{login}
GROUP BY weeks ORDER BY weeks desc ) ) ors )act on tm.tmDate=act.weeks join (SELECT @rownum := 0) AS rn order by tm.tmDate

SELECT tm.tmDate as date, IFNULL(TRUNCATE(IFNULL(comm.commTotal,0)/IFNULL(comm.avg,0),2) ,0)as ouHai, IFNULL(comm.dd,0) as myTotal FROM ( SELECT DATE_FORMAT(date_add(CURDATE(), INTERVAL i MONTH),'%Y-%m') AS tmDate FROM num a where a.i >#{num} and type='month' ) tm left join( select DATE_FORMAT(co.TIME_,'%Y-%m') as evDate, (select count(u.USER_ID) from rm_sys_user_info u where u.area='330304' and u.IDENTITY in
#{item}
) as avg, count(co.ID_) as commTotal, SUM(case when co.USER_ID_=#{handle} then 1 else 0 END )as dd from act_hi_comment co left join rm_sys_user_info us on co.USER_ID_=us.USER_MOBILE left join rm_task_info ta on co.PROC_INST_ID_=ta.PROC_INST_ID where DATE_FORMAT(co.TIME_,'%Y-%m-%d') > date_add(CURDATE(), interval 31*#{num} day) and ta.HANDLE_TIME is not null and us.area='330304' and us.IDENTITY in
#{item}
group by evDate ) comm on comm.evDate=tm.tmDate ORDER BY tm.tmDate

SELECT ta.TASK_NAME as taskName, case when ve.VENUES_NAME is not null then ve.VENUES_NAME else '' end as venuesName, case when ve.VENUES_ADDRES is not null then ve.VENUES_ADDRES else '' end as venuesAddres, case when ta.HANDLE_TIME is not null then '已完成' else '待完成' end as statTask, ta.PROC_INST_ID as procInstId, DATE_FORMAT(ta.LAUNCH_TIME,'%Y-%m-%d %H:%i:%s') as taskTime, case ta.EMERGENCY_LEVEL when '01' then '紧急' when '02' then '普通' else ta.EMERGENCY_LEVEL end as emeLevelCn, ta.EMERGENCY_LEVEL as emeLevel, ta.FLOW_TYPE as flowType, if(u.USER_NM is not null, u.USER_NM,if(m.MANAGER_CN_NM is not null,m.MANAGER_CN_NM,ta.LAUNCH_PERSON )) as launchPerson, '330000' as province, '330300' as city, ve.area as area, case ta.REL_VENUES_ID!=u.area when ta.REL_VENUES_ID=ve.VENUES_ID then ve.town when ve.VENUES_ID is null then ta.REL_VENUES_ID else '' end as town, TASK_CONTENT as taskContent FROM rm_task_info ta left join rm_venues_info ve on ve.VENUES_ID=ta.REL_VENUES_ID left join rm_sys_user_info u on u.USER_MOBILE=ta.LAUNCH_PERSON left join rm_venues_manager_info m on m.MANAGER_MOBILE=ta.LAUNCH_PERSON where 1=1
and ta.LAUNCH_PERSON = #{vo.searchTwo}
and ve.VENUES_NAME LIKE CONCAT('%',#{vo.venues},'%')
and ta.TASK_NAME LIKE CONCAT('%',#{vo.searchOne},'%')
order by statTask desc,ta.EMERGENCY_LEVEL ,taskTime desc limit #{vo.page},#{vo.size}

