mapper.update:
update act_hi_actinst set ASSIGNEE_=#{assignee}
where ACT_TYPE_='startEvent'
and PROC_INST_ID_=#{procInstId}

mapper.select:
SELECT ta.TASK_NAME as taskName, ve.VENUES_NAME as venuesName, ve.VENUES_ADDRES as venuesAddres, DATE_FORMAT(ta.LAUNCH_TIME,'%Y-%m-%d %H:%i:%s') as taskTime, DATE_FORMAT(ta.END_TIME,'%Y-%m-%d %H:%i:%s') as endTime, FLOW_TYPE as flowType, case when us.USER_NM is not null then us.USER_NM else ma.MANAGER_CN_NM end as launchPerson, case when ta.HANDLE_RESULTS is null then '0' else ta.HANDLE_RESULTS end as handleResults, ta.TASK_CONTENT as taskContent, case ta.EMERGENCY_LEVEL when '01' then '紧急' when '02' then '普通' else ta.EMERGENCY_LEVEL end as emeLevelCn, ta.EMERGENCY_LEVEL as emeLevel, ta.LAUNCH_PERSON as launchMobil, '330000' as province, '330300' as city, us.area as area, case ta.REL_VENUES_ID!=us.area when ta.REL_VENUES_ID=ve.VENUES_ID then ve.town when ve.VENUES_ID is null then ta.REL_VENUES_ID else '' end as town, ta.REL_EVENT_ID as eventId, ta.TASK_PICTURE as taskPicture, case when ac.END_TIME_ is null then '0' else '1' end as handleFlag, if(ev.PICTURES_PATH is null,'',ev.PICTURES_PATH) as fileId
FROM rm_task_info ta left join rm_venues_info ve on ve.VENUES_ID=ta.REL_VENUES_ID left join act_hi_actinst ac on ac.PROC_INST_ID_ = ta.PROC_INST_ID AND ac.ASSIGNEE_=#{login} left join rm_sys_user_info us on us.USER_MOBILE=ta.LAUNCH_PERSON left join rm_venues_manager_info ma on ma.MANAGER_MOBILE=ta.LAUNCH_PERSON left join RM_EVENT_INFO ev on ev.EVENT_ID=ta.REL_EVENT_ID where 1=1
and ta.PROC_INST_ID = #{procInstId}

SELECT ve.VENUES_NAME as venuesName, ve.VENUES_ADDRES as venuesAddres, DATE_FORMAT(ac.START_TIME_,'%Y-%m-%d %H:%i:%s') as taskTime, ev.ACCESS_NUMBER as accessNumber, ta.PROC_INST_ID as procInstId, ta.HANDLE_RESULTS as handleResults FROM rm_task_info ta join act_hi_actinst ac on ac.PROC_INST_ID_ = ta.PROC_INST_ID and ac.ASSIGNEE_=#{assignee} left join rm_event_info ev on ta.REL_EVENT_ID=ev.EVENT_ID left join rm_venues_info ve on ve.VENUES_ID=ta.REL_VENUES_ID where ta.FLOW_TYPE='07'
and ta.HANDLE_TIME is null
and ta.HANDLE_TIME is not null and ta.HANDLE_RESULTS=#{handleResults}

select

FROM rm_task_info ta
where ta.REL_EVENT_ID =#{eventId}

SELECT ta.TASK_NAME as taskName, case when ve.VENUES_NAME is not null then ve.VENUES_NAME else '' end as venuesName, case when ve.VENUES_ADDRES is not null then ve.VENUES_ADDRES else '' end as venuesAddres, '待完成' as statTask, ta.PROC_INST_ID as procInstId, DATE_FORMAT(ta.LAUNCH_TIME,'%Y-%m-%d %H:%i:%s') as taskTime, case ta.EMERGENCY_LEVEL when '01' then '紧急' when '02' then '普通' else ta.EMERGENCY_LEVEL end as emeLevelCn, if(u.USER_NM is not null, u.USER_NM,if(m.MANAGER_CN_NM is not null,m.MANAGER_CN_NM,ta.LAUNCH_PERSON )) as launchPerson, TASK_CONTENT as taskContent FROM rm_task_info ta
join act_hi_actinst ac on ac.PROC_INST_ID_ = ta.PROC_INST_ID and ac.END_TIME_ is null and ac.ASSIGNEE_= #{vo.searchTwo}
left join rm_venues_info ve on ve.VENUES_ID=ta.REL_VENUES_ID left join rm_sys_user_info u on u.USER_MOBILE=ta.LAUNCH_PERSON left join rm_venues_manager_info m on m.MANAGER_MOBILE=ta.LAUNCH_PERSON where ta.HANDLE_TIME is null
and ve.VENUES_NAME LIKE CONCAT('%',#{vo.venues},'%')
and ta.TASK_NAME LIKE CONCAT('%',#{vo.searchOne},'%')
order by ta.EMERGENCY_LEVEL ,ta.LAUNCH_TIME desc limit #{vo.page},#{vo.size}
