mapper.update:
update rm_event_info
set RESPONSIBLE_PERSON=#{responsiblePerson},
    EVENT_TYPE=#{eventType},
    EVENT_STATE=#{eventState},
    HANDLE_RESULTS=#{handleTesults}
where EVENT_ID=#{eventId}

mapper.select:
select EVENT_ID as eventId, ACCESS_NUMBER as accessNumber, WARN_TIME as warnTime, v.VENUES_ADDRES as venuesAddres, e.RESPONSIBLE_PERSON as responsiblePerson, case EVENT_TYPE when '01' then '火灾预警' when '02' then '人脸识别' when '03' then '任务预警' when '04' then '人流聚集' else '其他' end as eventType, case EVENT_STATE when '00' then '待查实' when '01' then '已完成' when '02' then '误报' else '其他' end as eventState, HANDLE_RESULTS as handleTesults, HANDLE_TIME as handleTime from RM_EVENT_INFO e left join RM_VENUES_INFO v on e.REL_VENUES_ID=v.VENUES_ID where 1=1
AND ACCESS_NUMBER LIKE CONCAT('%',#{accessNumber},'%')
order by HANDLE_TIME desc limit #{page},#{size}

select ev.WARN_TIME as warnTime, case ev.EVENT_TYPE when '01' then '火灾预警' when '02' then '人脸识别' when '03' then '任务预警' when '04' then '人流聚集' end as eventType, ve.VENUES_NAME as venuesName, ve.PICTURES_PATH as path from RM_EVENT_INFO ev left join RM_VENUES_INFO ve on ev.REL_VENUES_ID=ve.VENUES_ID where 1=1
AND ev.EVENT_STATE=#{eventState}
AND ev.EVENT_TYPE =#{eventType}
