mapper.update:
update rm_event_info
set RESPONSIBLE_PERSON=#{responsiblePerson},
    EVENT_TYPE=#{eventType},
    EVENT_STATE=#{eventState},
    HANDLE_RESULTS=#{handleTesults}
where EVENT_ID=#{eventId}

mapper.select:
select EVENT_ID as eventId, m.ACCESS_NUMBER as accessNumber, DATE_FORMAT(WARN_TIME,'%Y-%m-%d %H:%i:%s') as warnTime, ve.VENUES_ADDRES as venuesAddres, ve.VENUES_NAME as venuesName, e.RESPONSIBLE_PERSON as responsiblePerson, EVENT_TYPE as eventType, case EVENT_TYPE when '01' then '火灾预警' when '02' then '人脸识别' when '03' then '任务预警' when '04' then '人流聚集' else '其他' end as eventTypeNm, EVENT_STATE as eventState, case EVENT_STATE when '00' then '待查实' when '01' then '已完成' when '02' then '误报' else '其他' end as eventStateNm, HANDLE_RESULTS as handleTesults, DATE_FORMAT(HANDLE_TIME,'%Y-%m-%d %H:%i:%s') as handleTime, ve.PICTURES_PATH as picturesPath, ve.VENUES_NAME as venuesName, e.event_level as eventLevel, case event_level when '0' then '事件' when '1' then '次要' when '2' then '重要' when '3' then '严重' else '其他' end as eventLevelNm, m.MONITOR_URL as monitorUrl
from RM_EVENT_INFO e left join RM_VENUES_INFO ve on e.REL_VENUES_ID=ve.VENUES_ID left join rm_monitor_manage_info m on e.ACCESS_NUMBER=m.ACCESS_NUMBER where 1=1
and e.EVENT_TYPE=#{vo.searchOne}
AND ve.TOWN = #{vo.town}
AND ve.AREA = #{vo.area}
and ve.VENUES_ID IN
#{value}
ORDER BY WARN_TIME DESC limit #{vo.page},#{vo.size}

select e.EVENT_ID as eventId, e.ACCESS_NUMBER as accessNumber, e.DEVICE_NAME as deviceName, e.WARN_TIME as warnTime, ve.VENUES_ADDRES as venuesAddres, e.RESPONSIBLE_PERSON as responsiblePerson, case e.EVENT_TYPE when '01' then '火灾预警' when '02' then '人脸识别' when '03' then '任务预警' when '04' then '人流聚集' else '其他' end as eventType, e.EVENT_DATA as eventData, e.LOCATION as location, case e.EVENT_STATE when '00' then '待查实' when '01' then '已完成' when '02' then '误报' else '其他' end as eventState, e.HANDLE_RESULTS as handleTesults, e.HANDLE_TIME as handleTime from RM_EVENT_INFO e left join RM_VENUES_INFO ve on e.REL_VENUES_ID=ve.VENUES_ID where ve.VENUES_STATUS!='02'
AND e.ACCESS_NUMBER LIKE CONCAT('%',#{vo.searchOne},'%')
AND ve.TOWN = #{vo.town}
AND ve.AREA = #{vo.area}
and ve.VENUES_ID IN
#{value}
order by HANDLE_TIME desc limit #{vo.page},#{vo.size}

select
    count(e.EVENT_ID)
from RM_EVENT_INFO e
left join RM_VENUES_INFO ve on e.REL_VENUES_ID=ve.VENUES_ID
where 1=1

    AND  e.ACCESS_NUMBER LIKE CONCAT('%',#{vo.searchOne},'%')


    AND  ve.TOWN = #{vo.town}


    AND  ve.AREA = #{vo.area}


    and ve.VENUES_ID IN

        #{value}

select EVENT_ID as eventId, ev.WARN_TIME as warnTime, case ev.EVENT_TYPE when '01' then '火灾预警' when '02' then '人脸识别' when '03' then '任务预警' when '04' then '人流聚集' end as eventType, ve.VENUES_NAME as venuesName, ve.PICTURES_PATH as path, ev.EVENT_LEVEL as eventLevel, case ev.EVENT_TYPE when '01' then '紧急' else '普通' end as eventLevelCn from RM_EVENT_INFO ev left join RM_VENUES_INFO ve on ev.REL_VENUES_ID=ve.VENUES_ID where 1=1
AND ev.EVENT_STATE=#{vo.searchOne}
AND ev.EVENT_TYPE =#{vo.searchTwo}
AND ve.TOWN = #{vo.town}
AND ve.AREA = #{vo.area}
and ve.VENUES_ID IN
#{value}
ORDER BY ev.EVENT_TYPE limit #{vo.page},#{vo.size}

select
    count(ev.EVENT_ID)
from RM_EVENT_INFO ev
left join RM_VENUES_INFO ve on ev.REL_VENUES_ID=ve.VENUES_ID
where 1=1

    AND  ev.EVENT_STATE=#{vo.searchOne}


    AND  ev.EVENT_TYPE =#{vo.searchTwo}


    AND  ve.TOWN = #{vo.town}


    AND  ve.AREA = #{vo.area}


    and ve.VENUES_ID IN

        #{value}

select EVENT_ID as eventId, m.ACCESS_NUMBER as accessNumber, DATE_FORMAT(WARN_TIME,'%Y-%m-%d %H:%i:%s') as warnTime, v.VENUES_ADDRES as venuesAddres, e.RESPONSIBLE_PERSON as responsiblePerson, EVENT_TYPE as eventType, case EVENT_TYPE when '01' then '火灾预警' when '02' then '人脸识别' when '03' then '任务预警' when '04' then '人流聚集' else '其他' end as eventTypeNm, EVENT_STATE as eventState, case EVENT_STATE when '00' then '待查实' when '01' then '已完成' when '02' then '误报' else '其他' end as eventStateNm, HANDLE_RESULTS as handleTesults, DATE_FORMAT(HANDLE_TIME,'%Y-%m-%d %H:%i:%s') as handleTime, v.PICTURES_PATH as picturesPath, v.VENUES_NAME as venuesName, e.event_level as eventLevel, case event_level when '0' then '事件' when '1' then '次要' when '2' then '重要' when '3' then '严重' else '其他' end as eventLevelNm, m.MONITOR_URL as monitorUrl
from RM_EVENT_INFO e left join RM_VENUES_INFO v on e.REL_VENUES_ID=v.VENUES_ID left join rm_monitor_manage_info m on e.ACCESS_NUMBER=m.ACCESS_NUMBER left join RM_EVENT_NOTIFIED_INFO noti on noti.ref_event_id=e.EVENT_ID where e.EVENT_STATE in
#{eventState}
AND ( noti.NOTIFIED_USER =#{login} or NOTIFIED_STAFF =#{login} )
ORDER BY WARN_TIME DESC limit #{page},#{size}

select 
from rm_event_info

    where  EVENT_ID =#{eventId}

select

from rm_event_info e
where e.EVENT_STATE='00'

    and e.ACCESS_NUMBER=#{accessNumber}


    and e.EVENT_TYPE=#{eventType}


    and e.RESPONSIBLE_PERSON=#{responsiblePerson}


    and e.REL_VENUES_ID=#{relVenuesId}


    and e.LOCATION=#{location}
