mapper.update:
update rm_event_info
set RESPONSIBLE_PERSON=#{responsiblePerson},
    EVENT_TYPE=#{eventType},
    EVENT_STATE=#{eventState},
    HANDLE_RESULTS=#{handleResults}
where EVENT_ID=#{eventId}

mapper.select:
select EVENT_ID as eventId, WARN_TIME as warnTime, REL_VENUES_ID as relVenuesId, EVENT_TYPE as eventType, EVENT_LEVEL as eventLevel, EVENT_STATE as eventState from rm_event_info ev where 1=1
and ev.EVENT_ID=#{vo.searchOne}
order by WARN_TIME desc

select EVENT_ID as eventId, m.ACCESS_NUMBER as accessNumber, DATE_FORMAT(WARN_TIME,'%Y-%m-%d %H:%i:%s') as warnTime, ve.VENUES_ADDRES as venuesAddres, ve.VENUES_NAME as venuesName, e.RESPONSIBLE_PERSON as responsiblePerson, EVENT_TYPE as eventType, case EVENT_TYPE when '01' then '火灾预警' when '02' then '人脸识别' when '03' then '任务预警' when '04' then '人流聚集' else '其他' end as eventTypeNm, EVENT_STATE as eventState, case when EVENT_STATE in ('00','02') then '待完成' when EVENT_STATE in ('01','03','04') then '已完成' else '其他' end as eventStateNm, HANDLE_RESULTS as handleResults, DATE_FORMAT(HANDLE_TIME,'%Y-%m-%d %H:%i:%s') as handleTime, ve.PICTURES_PATH as picturesPath, ve.VENUES_NAME as venuesName, e.event_level as eventLevel, case event_level when '0' then '事件' when '1' then '次要' when '2' then '重要' when '3' then '严重' else '其他' end as eventLevelNm, m.MONITOR_URL as monitorUrl, ve.RELIGIOUS_SECT as sect from RM_EVENT_INFO e left join RM_VENUES_INFO ve on e.REL_VENUES_ID=ve.VENUES_ID left join rm_monitor_manage_info m on e.ACCESS_NUMBER=m.ACCESS_NUMBER where 1=1
and e.EVENT_TYPE=#{vo.searchOne}
and e.EVENT_STATE in
#{state}
AND ve.TOWN = #{vo.town}
AND ve.AREA = #{vo.area}
and ve.VENUES_ID IN
#{value}
ORDER BY WARN_TIME DESC limit #{vo.page},#{vo.size}

select e.EVENT_ID as eventId, e.ACCESS_NUMBER as accessNumber, e.DEVICE_NAME as deviceName, e.WARN_TIME as warnTime, ve.VENUES_ADDRES as venuesAddres, e.RESPONSIBLE_PERSON as responsiblePerson, case e.EVENT_TYPE when '01' then '火灾预警' when '02' then '人脸识别' when '03' then '任务预警' when '04' then '人流聚集' else '其他' end as eventType, e.EVENT_DATA as eventData, e.LOCATION as location, case e.EVENT_STATE when '00' then '待查实' when '01' then '已完成' when '02' then '误报' else '其他' end as eventState, e.HANDLE_RESULTS as handleResults, e.HANDLE_TIME as handleTime from RM_EVENT_INFO e left join RM_VENUES_INFO ve on e.REL_VENUES_ID=ve.VENUES_ID where ve.VENUES_STATUS!='02'
AND e.ACCESS_NUMBER LIKE CONCAT('%',#{vo.searchOne},'%')
AND ve.TOWN = #{vo.town}
AND ve.AREA = #{vo.area}
and ve.VENUES_ID IN
#{value}
order by HANDLE_TIME desc limit #{vo.page},#{vo.size}

select
    count(e.EVENT_ID)
from RM_EVENT_INFO e
left join RM_VENUES_INFO ve on e.REL_VENUES_ID=ve.VENUES_ID
where 1=1

    AND  e.ACCESS_NUMBER LIKE CONCAT('%',#{vo.searchOne},'%')


    AND  ve.TOWN = #{vo.town}


    AND  ve.AREA = #{vo.area}


    and ve.VENUES_ID IN

        #{value}

SELECT COUNT(EVENT_ID) AS total, SUM(CASE WHEN EVENT_TYPE = 01 THEN 1 ELSE 0 END) AS fire, SUM(CASE WHEN EVENT_TYPE = 02 THEN 1 ELSE 0 END) AS face, SUM(CASE WHEN EVENT_TYPE = 03 THEN 1 ELSE 0 END) AS task, SUM(CASE WHEN EVENT_TYPE = 04 THEN 1 ELSE 0 END) AS crowd FROM rm_event_info ev left join RM_VENUES_INFO ve on ev.REL_VENUES_ID=ve.VENUES_ID where ev.EVENT_TYPE!='05'
and ev.EVENT_STATE in
#{state}
AND ve.TOWN = #{vo.town}
AND ve.AREA = #{vo.area}
and ve.VENUES_ID IN
#{value}

select EVENT_ID as eventId, ev.WARN_TIME as warnTime, case ev.EVENT_TYPE when '01' then '火灾预警' when '02' then '人脸识别' when '03' then '任务预警' when '04' then '人流聚集' end as eventType, ve.VENUES_NAME as venuesName, ve.PICTURES_PATH as path, ev.EVENT_LEVEL as eventLevel, case ev.EVENT_LEVEL when '3' then '紧急' else '普通' end as eventLevelCn, ev.EVENT_RESOURCE as eventResource, case ev.EVENT_RESOURCE when '01' then 'AI预警' when '02' then '烟感' end as eventResourceCn, ve.RELIGIOUS_SECT as sect, ev.PICTURES_PATH as picturesPathEvent, ev.VIDEOS_PATH as videosPathEvent from rm_event_notified_info no left join RM_EVENT_INFO ev on no.REF_EVENT_ID=ev.EVENT_ID left join RM_VENUES_INFO ve on ev.REL_VENUES_ID=ve.VENUES_ID where no.NOTIFIED_FLAG='03'
AND ev.EVENT_STATE=#{vo.searchOne}
AND ev.EVENT_TYPE =#{vo.searchTwo}
AND ( no.NOTIFIED_USER LIKE CONCAT('%',#{vo.searchThree},'%') or no.NOTIFIED_MANAGER LIKE CONCAT('%',#{vo.searchThree},'%') )
ORDER BY ev.EVENT_LEVEL desc,ev.WARN_TIME desc limit #{vo.page},#{vo.size}

select count(ev.EVENT_ID) from rm_event_notified_info no left join RM_EVENT_INFO ev on no.REF_EVENT_ID=ev.EVENT_ID left join RM_VENUES_INFO ve on ev.REL_VENUES_ID=ve.VENUES_ID where no.NOTIFIED_FLAG='01'
AND ev.EVENT_STATE=#{vo.searchOne}
AND ev.EVENT_TYPE =#{vo.searchTwo}
AND ( no.NOTIFIED_USER LIKE CONCAT('%',#{vo.searchThree},'%') or no.NOTIFIED_MANAGER LIKE CONCAT('%',#{vo.searchThree},'%') )

select EVENT_ID as eventId, DATE_FORMAT(WARN_TIME,'%Y-%m-%d %H:%i:%s') as warnTime, v.VENUES_ADDRES as venuesAddres, ev.RESPONSIBLE_PERSON as responsiblePerson, EVENT_TYPE as eventType, case EVENT_TYPE when '01' then '火灾预警' when '02' then '人脸识别' when '03' then '任务预警' when '04' then '人流聚集' else '其他' end as eventTypeNm, EVENT_STATE as eventState, case EVENT_STATE when '00' then '待查实' when '01' then '已完成' when '02' then '误报' else '其他' end as eventStateNm, HANDLE_RESULTS as handleResults, DATE_FORMAT(HANDLE_TIME,'%Y-%m-%d %H:%i:%s') as handleTime, v.PICTURES_PATH as picturesPath, v.VENUES_NAME as venuesName, ev.event_level as eventLevel, case event_level when '0' then '普通' when '1' then '普通' when '2' then '普通' when '3' then '紧急' else '普通' end as eventLevelNm, v.PICTURES_PATH as venuesPicture
from RM_EVENT_NOTIFIED_INFO no left join RM_EVENT_INFO ev on no.ref_event_id=ev.EVENT_ID left join RM_VENUES_INFO v on ev.REL_VENUES_ID=v.VENUES_ID where ev.EVENT_STATE in
#{value}
AND no.NOTIFIED_MANAGER LIKE CONCAT('%',#{vo.searchOne},'%')
ORDER BY ev.event_level desc,ev.WARN_TIME desc limit #{vo.page},#{vo.size}

select count(distinct EVENT_ID) from RM_EVENT_NOTIFIED_INFO no left join RM_EVENT_INFO ev on no.ref_event_id=ev.EVENT_ID where ev.EVENT_STATE in
#{value}
AND no.NOTIFIED_MANAGER LIKE CONCAT('%',#{vo.searchOne},'%')

mapper.update:
update rm_event_info 
set EVENT_STATE=#{eventState},
    HANDLE_RESULTS=#{handleResults},
    HANDLE_TIME=#{handleTime}
where EVENT_ID=#{eventId}

mapper.select:
select 
from rm_event_info

    where  EVENT_ID =#{eventId}

SELECT tm.tmDate as date,IFNULL(ev.sum,0) as count
FROM (
    SELECT left(date_add(DATE_SUB(CURDATE(),INTERVAL dayofyear(now())-1 DAY), interval i month),7) AS tmDate
    FROM num a where a.i > #{vo.size} and type='month'
) tm
LEFT JOIN (
    select
        DATE_FORMAT(ev.WARN_TIME,'%Y-%m') evDate,count(ev.EVENT_ID) sum
    from rm_event_info ev
    left join rm_venues_info ve on ve.VENUES_ID=ev.REL_VENUES_ID
    where ve.VENUES_STATUS='01'
        and ev.EVENT_TYPE=#{vo.searchOne}
        and  DATE_FORMAT(ev.WARN_TIME,'%Y-%m-%d') > date_add(CURDATE(), interval 31*#{vo.size}  day)

            AND  ve.TOWN = #{vo.town}


            AND  ve.AREA = #{vo.area}


            and ve.VENUES_ID IN

                #{value}


    group by evDate
) ev on tm.tmDate= ev.evDate
ORDER BY tm.tmDate

SELECT DATE_FORMAT(tm.tmDate,'%Y-%m-%d') as date,IFNULL(ev.sum,0)  as count
FROM(
    SELECT date_add(CURDATE(), interval i day) AS tmDate FROM num a where a.i > #{vo.size} and type='day'
) tm
LEFT JOIN(
    select DATE_FORMAT(WARN_TIME,'%Y-%m-%d') evDate,count(EVENT_ID) as sum
    from rm_event_info ev
    left join rm_venues_info ve on ve.VENUES_ID=ev.REL_VENUES_ID
    where ve.VENUES_STATUS='01'
        and EVENT_TYPE=#{vo.searchOne}
        and DATE_FORMAT(WARN_TIME,'%Y-%m-%d') > date_add(CURDATE(), interval #{vo.size}  day)

            AND  ve.TOWN = #{vo.town}


            AND  ve.AREA = #{vo.area}


            and ve.VENUES_ID IN

                #{value}


    group by evDate
) ev on tm.tmDate= ev.evDate
ORDER BY tm.tmDate

SELECT (@rownum := @rownum + 1) AS date,
        IFNULL(zh.sum,0)  as count
FROM(
    SELECT i AS tmDate FROM num a where a.i > #{vo.size} and type='day'
) tm
LEFT JOIN
(
SELECT ors.weeks,ors.sum
from (
    (
        select  WEEK(ev.WARN_TIME,3) -(WEEK(CURDATE(),3)) as weeks,
                count( ev.EVENT_ID) as sum
        from rm_event_info ev
        left join rm_venues_info ve on ve.VENUES_ID=ev.REL_VENUES_ID
        where ve.VENUES_STATUS='01'
        and DATE_FORMAT(ev.WARN_TIME,'%Y')=DATE_FORMAT(CURDATE(),'%Y')
        and DATE_FORMAT(ev.WARN_TIME,'%Y-%m-%d') > date_add(CURDATE(), interval (#{vo.size} +1)*7 day)
        and ev.EVENT_TYPE=#{vo.searchOne}

            AND  ve.TOWN = #{vo.town}


            AND  ve.AREA = #{vo.area}


            and ve.VENUES_ID IN

                #{value}


        GROUP BY weeks
        ORDER BY weeks
    )union all(
        select
            (WEEK(ev.WARN_TIME,3) -(select WEEK(num.REMARK,3) as h from num where type='lastYear') - (WEEK(CURDATE(),3))) as weeks,
            count( ev.EVENT_ID) as sum
        from rm_event_info  ev
        left join rm_venues_info ve on ve.VENUES_ID=ev.REL_VENUES_ID
        where ve.VENUES_STATUS='01'
        and DATE_FORMAT(ev.WARN_TIME,'%Y')=DATE_FORMAT(CURDATE(),'%Y')-1
        and DATE_FORMAT(ev.WARN_TIME,'%Y-%m-%d') > (select date_add(subdate(curdate(),date_format(curdate(),'%w')-1),interval (#{vo.size} +1)*7 day)from dual)
        and EVENT_TYPE=#{vo.searchOne}

            AND  ve.TOWN = #{vo.town}


            AND  ve.AREA = #{vo.area}


        and ve.VENUES_ID IN

            #{value}


        GROUP BY weeks
        ORDER BY weeks desc
    )
) ors
)zh on tm.tmDate=zh.weeks
join (SELECT
@rownum := 0) AS rn
order by tm.tmDate

SELECT tm.tmDate as date, IFNULL(ev.fire,0) as fire, IFNULL(ev.face,0) as face, IFNULL(ev.crowd,0) as crowd FROM ( SELECT left(date_add(DATE_SUB(CURDATE(),INTERVAL dayofyear(now())-1 DAY), interval i+2 month),7) AS tmDate FROM num a where a.i > #{vo.size} and type='month' ) tm LEFT JOIN ( select DATE_FORMAT(WARN_TIME,'%Y-%m') evDate, SUM(CASE WHEN ev.EVENT_TYPE = 01 THEN 1 ELSE 0 END) AS fire, SUM(CASE WHEN ev.EVENT_TYPE = 02 THEN 1 ELSE 0 END) AS face, SUM(CASE WHEN ev.EVENT_TYPE = 03 THEN 1 ELSE 0 END) AS task, SUM(CASE WHEN ev.EVENT_TYPE = 04 THEN 1 ELSE 0 END) AS crowd from rm_event_info ev left join rm_venues_info ve on ve.VENUES_ID=ev.REL_VENUES_ID where ve.VENUES_STATUS='01' and DATE_FORMAT(ev.WARN_TIME,'%Y-%m-%d') > date_add(CURDATE(), interval 31*#{vo.size} day)
AND ve.TOWN = #{vo.town}
AND ve.AREA = #{vo.area}
and ve.VENUES_ID IN
#{value}
group by evDate ) ev on tm.tmDate= ev.evDate ORDER BY tm.tmDate

SELECT DATE_FORMAT(tm.tmDate,'%Y-%m-%d') as date, IFNULL(ev.fire,0) as fire, IFNULL(ev.face,0) as face, IFNULL(ev.crowd,0) as crowd FROM( SELECT date_add(CURDATE(), interval i day) AS tmDate FROM num a where a.i > #{vo.size} and type='day' ) tm LEFT JOIN( select DATE_FORMAT(ev.WARN_TIME,'%Y-%m-%d') evDate, SUM(CASE WHEN ev.EVENT_TYPE = 01 THEN 1 ELSE 0 END) AS fire, SUM(CASE WHEN ev.EVENT_TYPE = 02 THEN 1 ELSE 0 END) AS face, SUM(CASE WHEN ev.EVENT_TYPE = 03 THEN 1 ELSE 0 END) AS task, SUM(CASE WHEN ev.EVENT_TYPE = 04 THEN 1 ELSE 0 END) AS crowd from rm_event_info ev left join rm_venues_info ve on ve.VENUES_ID=ev.REL_VENUES_ID where ve.VENUES_STATUS='01' and DATE_FORMAT(ev.WARN_TIME,'%Y-%m-%d') > date_add(CURDATE(), interval #{vo.size} day)
AND ve.TOWN = #{vo.town}
AND ve.AREA = #{vo.area}
and ve.VENUES_ID IN
#{value}
group by evDate ) ev on tm.tmDate= ev.evDate ORDER BY tm.tmDate

SELECT (@rownum := @rownum + 1) AS date, IFNULL(zh.fire,0) as fire, IFNULL(zh.face,0) as face, IFNULL(zh.crowd,0) as crowd FROM( SELECT i AS tmDate FROM num a where a.i > #{vo.size} and type='day' ) tm LEFT JOIN ( SELECT ors.weeks, IFNULL(ors.fire,0) as fire, IFNULL(ors.face,0) as face, IFNULL(ors.crowd,0) as crowd from ( ( select WEEK(ev.WARN_TIME,3) -(WEEK(CURDATE(),3)) as weeks, SUM(CASE WHEN ev.EVENT_TYPE = 01 THEN 1 ELSE 0 END) AS fire, SUM(CASE WHEN ev.EVENT_TYPE = 02 THEN 1 ELSE 0 END) AS face, SUM(CASE WHEN ev.EVENT_TYPE = 03 THEN 1 ELSE 0 END) AS task, SUM(CASE WHEN ev.EVENT_TYPE = 04 THEN 1 ELSE 0 END) AS crowd from rm_event_info ev left join rm_venues_info ve on ve.VENUES_ID=ev.REL_VENUES_ID where ve.VENUES_STATUS='01' and DATE_FORMAT(ev.WARN_TIME,'%Y')=DATE_FORMAT(CURDATE(),'%Y') and DATE_FORMAT(ev.WARN_TIME,'%Y-%m-%d') > date_add(CURDATE(), interval (#{vo.size}+1)*7 day)
AND ve.TOWN = #{vo.town}
AND ve.AREA = #{vo.area}
and ve.VENUES_ID IN
#{value}
GROUP BY weeks ORDER BY weeks )union all( select (WEEK(ev.WARN_TIME,3) -(select WEEK(num.REMARK,3) as h from num where type='lastYear') - (WEEK(CURDATE(),3))) as weeks, SUM(CASE WHEN ev.EVENT_TYPE = 01 THEN 1 ELSE 0 END) AS fire, SUM(CASE WHEN ev.EVENT_TYPE = 02 THEN 1 ELSE 0 END) AS face, SUM(CASE WHEN ev.EVENT_TYPE = 03 THEN 1 ELSE 0 END) AS task, SUM(CASE WHEN ev.EVENT_TYPE = 04 THEN 1 ELSE 0 END) AS crowd from rm_event_info ev left join rm_venues_info ve on ve.VENUES_ID=ev.REL_VENUES_ID where ve.VENUES_STATUS='01' and DATE_FORMAT(ev.WARN_TIME,'%Y')=DATE_FORMAT(CURDATE(),'%Y')-1 and DATE_FORMAT(ev.WARN_TIME,'%Y-%m-%d') > (select date_add(subdate(curdate(),date_format(curdate(),'%w')-1),interval 7*(#{vo.size}+1)-1 day)from dual)
AND ve.TOWN = #{vo.town}
AND ve.AREA = #{vo.area}
and ve.VENUES_ID IN
#{value}
GROUP BY weeks ORDER BY weeks desc ) ) ors )zh on tm.tmDate=zh.weeks join (SELECT @rownum := 0) AS rn order by tm.tmDate

select

from rm_event_info e
where e.EVENT_STATE='00'

    and e.ACCESS_NUMBER=#{accessNumber}


    and e.EVENT_TYPE=#{eventType}


    and e.RESPONSIBLE_PERSON=#{responsiblePerson}


    and e.REL_VENUES_ID=#{relVenuesId}


    and e.LOCATION=#{location}

select ev.EVENT_ID as eventId, DATE_FORMAT(ev.WARN_TIME,'%Y-%m-%d %H:%i:%s') as warnTime, ve.VENUES_NAME as venuesName, ev.EVENT_TYPE as eventType, case ev.EVENT_TYPE when '01' then '火灾预警' when '02' then '人脸识别' when '03' then '任务预警' when '04' then '人流聚集' else '其他' end as eventTypeNm, ve.PICTURES_PATH as venuesPicture, ev.EVENT_STATE as eventState, DATE_FORMAT(ev.HANDLE_TIME,'%Y-%m-%d %H:%i:%s') as handleTime, case when us.USER_NM is not null then us.USER_NM else fzz.MANAGER_CN_NM end as handlePerson, case when ta.PROC_INST_ID is not null then ta.PROC_INST_ID else '' end as procInstId, ta.EMERGENCY_LEVEL as emergencyLevel, case ta.EMERGENCY_LEVEL when '01' then '紧急' when '02' then '普通' else ta.EMERGENCY_LEVEL end as emergencyLevelCn from RM_EVENT_INFO ev left join RM_VENUES_INFO ve on ev.REL_VENUES_ID=ve.VENUES_ID left join rm_sys_user_info us on us.USER_MOBILE=ev.HANDLE_RESULTS left join rm_venues_manager_info fzz on fzz.MANAGER_MOBILE=ev.HANDLE_RESULTS left join rm_task_info ta on ta.REL_EVENT_ID=ev.EVENT_ID where ev.EVENT_TYPE!='05'
and ev.EVENT_TYPE=#{vo.searchOne}
and ev.EVENT_STATE in
#{state}
AND ve.TOWN = #{vo.town}
AND ve.AREA = #{vo.area}
and ve.VENUES_ID IN
#{value}
ORDER BY ev.EVENT_LEVEL desc,ev.WARN_TIME desc
ORDER BY ev.WARN_TIME desc
limit #{vo.page},#{vo.size}

select
    count(ev.EVENT_ID)
from RM_EVENT_INFO ev
left join RM_VENUES_INFO ve on ev.REL_VENUES_ID=ve.VENUES_ID
where ev.EVENT_TYPE!='05'

    and ev.EVENT_TYPE=#{vo.searchOne}


    and ev.EVENT_STATE in

        #{state}




    AND  ve.TOWN = #{vo.town}


    AND  ve.AREA = #{vo.area}


    and ve.VENUES_ID IN

        #{value}

select
    count(men_id)
from rm_event_report_men
where PROC_INST_ID=#{procInstId}
and REPORT_MEN =#{login}
