mapper.select:
select  from rm_monitor_manage_info
where state=#{state}
ORDER BY MONITOR_ID DESC

SELECT FROM rm_monitor_manage_info
INNER JOIN rm_venues_info ON rm_venues_info.VENUES_ID=rm_monitor_manage_info.REL_VENUES_ID
WHERE state=#{state}

select MONITOR_ID as monitorId, ACCESS_NUMBER as accessNumber, MONITOR_URL as monitorURL, CASE STATE WHEN '01' THEN '在线' ELSE '报修' END as state, FUNCTION_TYPE as functionType, v.VENUES_ADDRES as venuesAddres, r.CREATOR as creator, r.CREATE_TIME as createTime, r.LAST_MODIFIER as lastModifier, r.LAST_MODIFY_TIME as lastModifyTime from RM_MONITOR_MANAGE_INFO r left join RM_VENUES_INFO v on r.REL_VENUES_ID=v.VENUES_ID where 1=1
AND ACCESS_NUMBER LIKE CONCAT('%',#{accessNumber},'%')
order by r.LAST_MODIFY_TIME desc limit #{page},#{size}
