mapper.select:
SELECT  COUNT(MONITOR_ID) AS total,
        SUM(CASE WHEN STATE =01 THEN 1 ELSE 0 END) AS onLine,
        SUM(CASE WHEN STATE =02 THEN 1 ELSE 0 END) AS repair
FROM rm_monitor_manage_info mo
left join rm_venues_info ve on ve.VENUES_ID=mo.REL_VENUES_ID
where ve.VENUES_STATUS='01'

    AND  ve.TOWN = #{vo.town}


    AND  ve.AREA = #{vo.area}


    and ve.VENUES_ID IN

        #{value}

select  from rm_monitor_manage_info
where state=#{state}
ORDER BY MONITOR_ID DESC

SELECT FROM rm_monitor_manage_info
INNER JOIN rm_venues_info ON rm_venues_info.VENUES_ID=rm_monitor_manage_info.REL_VENUES_ID
WHERE state=#{state}

select count(r.MONITOR_ID)
from RM_MONITOR_MANAGE_INFO r
left join RM_VENUES_INFO ve on r.REL_VENUES_ID=ve.VENUES_ID
where 1=1

    AND  r.ACCESS_NUMBER LIKE CONCAT('%',#{vo.searchOne},'%')


    AND  ve.TOWN = #{vo.town}


    AND  ve.AREA = #{vo.area}


    and ve.VENUES_ID IN

        #{value}

select MONITOR_ID as monitorId, ACCESS_NUMBER as accessNumber, MONITOR_URL as monitorUrl, CASE STATE WHEN '01' THEN '在线' ELSE '报修' END as state, FUNCTION_TYPE as functionType, ve.VENUES_NAME as venuesAddres, r.CREATOR as creator, r.CREATE_TIME as createTime, r.LAST_MODIFIER as lastModifier, r.LAST_MODIFY_TIME as lastModifyTime from RM_MONITOR_MANAGE_INFO r left join RM_VENUES_INFO ve on r.REL_VENUES_ID=ve.VENUES_ID where 1=1
AND r.ACCESS_NUMBER LIKE CONCAT('%',#{vo.searchOne},'%')
AND ve.TOWN = #{vo.town}
AND ve.AREA = #{vo.area}
and ve.VENUES_ID IN
#{value}
order by r.LAST_MODIFY_TIME desc limit #{vo.page},#{vo.size}

select ve.VENUES_ID as venuesId, ve.VENUES_NAME as venuesName, ve.VENUES_ADDRES as venuesAddres, ve.ORGANIZATION as organization, ve.REGISTER_NBR as registerNbr, res.MANAGER_CN_NM as responsiblePerson, res.MANAGER_MOBILE as responsibleMobile, gr.MANAGER_CN_NM as members, gr.MANAGER_MOBILE as membersMobile, lia.MANAGER_CN_NM as liaisonMan, lia.MANAGER_MOBILE as liaisonManMobile, (SELECT GROUP_CONCAT(ACCESS_NUMBER) FROM rm_monitor_manage_info mo where mo.REL_VENUES_ID=ve.VENUES_ID ) as monitors from RM_VENUES_INFO ve left join rm_venues_manager_info res on ve.RESPONSIBLE_PERSON=res.MANAGER_ID and res.MANAGER_TYPE_CD='01' left join rm_venues_manager_info gr on ve.GROUP_MEMBERS=gr.MANAGER_ID and gr.MANAGER_TYPE_CD='02' left join rm_venues_manager_info lia on ve.LIAISON_MAN=lia.MANAGER_ID and lia.MANAGER_TYPE_CD='03' where VENUES_STATUS!='02'
AND ve.VENUES_NAME LIKE CONCAT('%',#{vo.searchOne},'%')
AND ve.TOWN = #{vo.town}
AND ve.AREA = #{vo.area}
and ve.VENUES_ID IN
#{value}
order by ve.VENUES_ID desc limit #{vo.page},#{vo.size}

select
    count(ve.VENUES_ID)
from RM_VENUES_INFO ve
where VENUES_STATUS!='02'

    AND  ve.VENUES_NAME LIKE CONCAT('%',#{vo.searchOne},'%')


    AND  ve.TOWN = #{vo.town}


    AND  ve.AREA = #{vo.area}


    and ve.VENUES_ID IN

        #{value}

select ve.VENUES_ID as VenuesId, m.TASK_ID as taskId, m.ACCESS_NUMBER as accessNumber, ve.VENUES_NAME as venuesName, ve.VENUES_ADDRES as venuesAddres, m.CREATE_TIME as time from rm_monitor_manage_info m left join RM_VENUES_INFO ve on ve.VENUES_ID=m.rel_venues_id where VENUES_STATUS!='02'
AND ( m.ACCESS_NUMBER LIKE CONCAT('%',#{vo.searchOne},'%') or ve.VENUES_NAME LIKE CONCAT('%',#{vo.searchOne},'%'))
AND m.STATE =#{vo.searchTwo}
AND ve.TOWN = #{vo.town}
AND ve.AREA = #{vo.area}
and ve.VENUES_ID IN
#{value}
order by m.LAST_MODIFY_TIME limit #{vo.page},#{vo.size}

select
    count(m.MONITOR_ID)
from rm_monitor_manage_info m
left join  RM_VENUES_INFO ve on ve.VENUES_ID=m.rel_venues_id
where VENUES_STATUS!='02'

    AND ( m.ACCESS_NUMBER LIKE CONCAT('%',#{vo.searchOne},'%')
    or  ve.VENUES_NAME LIKE CONCAT('%',#{vo.searchOne},'%'))


    AND  m.STATE =#{vo.searchTwo}


    AND  ve.TOWN = #{vo.town}


    AND  ve.AREA = #{vo.area}


    and ve.VENUES_ID IN

        #{value}

select ve.VENUES_ID as venuesId, ve.VENUES_NAME as venuesName, ve.VENUES_ADDRES as venuesAddres, ve.ORGANIZATION as organization, ve.REGISTER_NBR as registerNbr, ve.VENUES_PHONE as venuesPhone, res.MANAGER_CN_NM as responsiblePerson, res.MANAGER_MOBILE as responsibleMobile, gr.MANAGER_CN_NM as members, gr.MANAGER_MOBILE as membersMobile, lia.MANAGER_CN_NM as liaisonMan, lia.MANAGER_MOBILE as liaisonManMobile, m.ACCESS_NUMBER as accessNumber, m.MONITOR_URL as monitorUrl, m.STATE as monitorStatus, (SELECT GROUP_CONCAT(ACCESS_NUMBER) FROM rm_monitor_manage_info mo where mo.REL_VENUES_ID=m.REL_VENUES_ID ) as monitors from rm_monitor_manage_info m left join RM_VENUES_INFO ve on ve.VENUES_ID=m.rel_venues_id left join rm_venues_manager_info res on ve.RESPONSIBLE_PERSON=res.MANAGER_ID and res.MANAGER_TYPE_CD='01' left join rm_venues_manager_info gr on ve.GROUP_MEMBERS=gr.MANAGER_ID and gr.MANAGER_TYPE_CD='02' left join rm_venues_manager_info lia on ve.LIAISON_MAN=lia.MANAGER_ID and lia.MANAGER_TYPE_CD='03' where 1=1
AND m.ACCESS_NUMBER =#{accessNumber}
AND ve.VENUES_ID =#{venuesId}

select distinct m.ACCESS_NUMBER as accessNumber, m.MONITOR_URL as monitorUrl, m.STATE as status, case m.STATE when '01' then '完好' when '02' then '损坏' else m.STATE end as statusCn from rm_monitor_manage_info m left join rm_monitor_manage_info mo on mo.rel_venues_id =m.rel_venues_id left join RM_VENUES_INFO ve on ve.VENUES_ID=m.rel_venues_id where 1=1
AND mo.ACCESS_NUMBER =#{accessNumber}
AND ve.VENUES_ID =#{venuesId}
