mapper.select:
select NEWS_ID as newsId, NEWS_VIEWS as newsViews, NEWS_TITLE as newsTitle, NEWS_KEYWORD as newsKeyword, NEWS_CONTENT as newsContent, NEWS_FROM as newsFrom, NEWS_REF as newsRef, dict.DICT_CN_DESC as newsType, NEWS_DOWN as newsDown, NEWS_PICTURES_PATH as newsPicturesPath, CREATOR as creator, NEWS_FOR as newsFor, DATE_FORMAT(news.CREATE_TIME,'%Y-%m-%d %H:%i:%s') as createTime, DATE_FORMAT(news.CREATE_TIME,'%Y') as releaseYear, DATE_FORMAT(news.CREATE_TIME,'%m') as releaseMonth, DATE_FORMAT(news.CREATE_TIME,'%d') as releaseDay from RM_NEWS_INFO news LEFT JOIN rm_sys_dict dict on news.NEWS_TYPE=dict.DICT_CD where NEWS_DOWN='1'
AND NEWS_TITLE LIKE CONCAT('%',#{vo.searchOne},'%')
AND NEWS_REF_TYPE =#{vo.searchTwo}
AND NEWS_FOR !=#{vo.searchThree}
AND NEWS_TYPE =#{vo.searchFour}
order by CREATE_TIME desc limit #{vo.page},#{vo.size}

select
    count(NEWS_ID)
from RM_NEWS_INFO
where NEWS_DOWN='1'

    AND  NEWS_TITLE LIKE CONCAT('%',#{vo.searchOne},'%')


    AND  NEWS_REF_TYPE =#{vo.searchTwo}


    AND  NEWS_FOR !=#{vo.searchThree}


    AND  NEWS_TYPE =#{vo.searchFour}

mapper.update:
update RM_NEWS_INFO
set
    NEWS_DOWN=#{newsDown},
    LAST_MODIFIER=#{lastModifier},
    LAST_MODIFY_TIME=#{lastModifyTime}
WHERE NEWS_ID=#{newsId}

mapper.select:
select NEWS_ID as newsId, NEWS_VIEWS as newsViews, NEWS_TITLE as newsTitle, NEWS_KEYWORD as newsKeyword, NEWS_CONTENT as newsContent, NEWS_FROM as newsFrom, NEWS_REF as newsRef, dict.DICT_CN_DESC as newsType, NEWS_DOWN as newsDown, NEWS_PICTURES_PATH as newsPicturesPath, CREATOR as creator, NEWS_FOR as newsFor, DATE_FORMAT(news.CREATE_TIME,'%Y-%m-%d %H:%i:%s') as createTime, case NEWS_REF_TYPE when '01' then '一般新闻' when '02' then '图片新闻' else NEWS_REF_TYPE end as newsRefType, case NEWS_TYPE when '01' then '宗教文化' when '02' then '政策学习' when '03' then '瓯海动态' else NEWS_TYPE end as newsType from RM_NEWS_INFO news LEFT JOIN rm_sys_dict dict on news.NEWS_TYPE=dict.DICT_CD where 1=1
AND NEWS_TITLE LIKE CONCAT('%',#{vo.searchOne},'%')
AND NEWS_REF_TYPE =#{vo.searchTwo}
AND NEWS_FOR =#{vo.searchThree}
AND NEWS_TYPE =#{vo.searchFour}
order by NEWS_DOWN desc,CREATE_TIME desc limit #{vo.page},#{vo.size}

select
count(NEWS_ID)
from RM_NEWS_INFO
where 1=1

    AND  NEWS_TITLE LIKE CONCAT('%',#{vo.searchOne},'%')


    AND  NEWS_REF_TYPE =#{vo.searchTwo}


    AND  NEWS_FOR =#{vo.searchThree}


    AND  NEWS_TYPE =#{vo.searchFour}
