mapper.select:
select NEWS_ID as newsId, NEWS_VIEWS as newsViews, NEWS_TITLE as newsTitle, NEWS_KEYWORD as newsKeyword, NEWS_CONTENT as newsContent, NEWS_FROM as newsFrom, NEWS_REF as newsRef, dict.DICT_CN_DESC as newsType, NEWS_DOWN as newsDown, NEWS_PICTURES_PATH as newsPicturesPath, CREATOR as creator, DATE_FORMAT(CREATE_TIME,'%Y-%m-%d %H:%i:%s') as createTime, NEWS_FOR as newsFor from RM_NEWS_INFO news LEFT JOIN rm_sys_dict dict on news.NEWS_TYPE=dict.DICT_CD where NEWS_DOWN='1'
AND NEWS_TITLE LIKE CONCAT('%',#{newsTitle},'%')
AND NEWS_FOR =#{newsFor}
order by LAST_MODIFY_TIME desc limit #{page},#{size}

select count(NEWS_ID) from RM_NEWS_INFO where NEWS_DOWN='1'
AND NEWS_TITLE LIKE CONCAT('%',#{newsTitle},'%')
AND NEWS_FOR =#{newsFor}
