mapper.select:
select VENUES_ID as venuesId , VENUES_NAME as venuesName, VENUES_ADDRES as venuesAddres from RM_VENUES_INFO where VENUES_STATUS!='02'
and VENUES_NAME LIKE concat('%',#{search},'%')
and TOWN =#{town}

select
    VENUES_ID as venuesId ,
    VENUES_NAME as venuesName
from RM_VENUES_INFO ve
where VENUES_STATUS!='02'

    and( RESPONSIBLE_PERSON LIKE concat('%',#{vo.searchOne},'%')
            or VENUES_ADDRES LIKE concat('%',#{vo.searchOne},'%')
            or VENUES_NAME LIKE concat('%',#{vo.searchOne},'%')
            or VENUES_ID=#{vo.searchOne}
    )


    AND  ve.TOWN = #{vo.town}


    AND  ve.AREA = #{vo.area}


    and ve.VENUES_ID IN

        #{value}

select

from RM_VENUES_INFO


        RELIGIOUS_SECT=#{religiousSect}

select

from RM_VENUES_INFO
where VENUES_ID=#{venuesId}

SELECT COUNT(VENUES_ID) AS total, SUM(CASE WHEN RELIGIOUS_SECT = '100100000005' THEN 1 ELSE 0 END) AS Islamism, SUM(CASE WHEN RELIGIOUS_SECT = '100100000003' THEN 1 ELSE 0 END) AS Christianity, SUM(CASE WHEN RELIGIOUS_SECT = '100100000004' THEN 1 ELSE 0 END) AS Catholicism, SUM(CASE WHEN RELIGIOUS_SECT = '100100000001' THEN 1 ELSE 0 END) AS Buddhism, SUM(CASE WHEN RELIGIOUS_SECT = '100100000002' THEN 1 ELSE 0 END) AS Taoism FROM rm_venues_info ve where ve.VENUES_STATUS='01'
AND ve.TOWN = #{vo.town}
AND ve.AREA = #{vo.area}
and ve.VENUES_ID IN
#{value}

SELECT
    ve.VENUES_NAME as venuesName,
    ma.MANAGER_CN_NM as  managerCnNm,
    ma.MANAGER_MOBILE as managerMobile
FROM rm_venues_info ve
left join rm_venues_manager_info ma on ve.RESPONSIBLE_PERSON=ma.manager_id
where ve.VENUES_STATUS='01'

    AND  ve.RELIGIOUS_SECT  = #{vo.searchOne}


    AND  ve.TOWN = #{vo.town}


    AND  ve.AREA = #{vo.area}


    and ve.VENUES_ID IN

        #{value}


order by ve.LAST_MODIFY_TIME desc,ve.VENUES_ID asc
limit #{vo.page},#{vo.size}

SELECT
    count(ve.venues_id)
FROM rm_venues_info ve
where ve.VENUES_STATUS='01'

    AND  ve.RELIGIOUS_SECT  = #{vo.searchOne}


    AND  ve.TOWN = #{vo.town}


    AND  ve.AREA = #{vo.area}


    and ve.VENUES_ID IN

        #{value}

select count(VENUES_ID)
from RM_VENUES_INFO ve
left join rm_venues_manager_info fz on fz.MANAGER_ID=ve.RESPONSIBLE_PERSON and fz.MANAGER_TYPE_CD='01' and fz.VALID_IND='1'
where VENUES_STATUS!='02'

    AND  ve.VENUES_NAME LIKE CONCAT('%',#{venue.venuesName},'%')


    AND  fz.MANAGER_CN_NM LIKE CONCAT('%',#{venue.responsiblePerson},'%')


    AND  ve.RELIGIOUS_SECT = #{venue.religiousSect}


    AND  ve.VENUES_PHONE LIKE CONCAT('%',#{venue.venuesPhone},'%')


    AND  ve.TOWN = #{venue.town}


    AND  ve.AREA = #{venue.area}


    and ve.VENUES_ID IN

        #{value}

select ve.VENUES_ID as venuesId , ve.VENUES_NAME as venuesName, d.DICT_CN_DESC as religiousSect, ve.REGISTER_NBR as registerNbr, ve.VENUES_PHONE as venuesPhone, ve.ORGANIZATION as organization, ve.VENUES_ADDRES as venuesAddres, ve.PICTURES_PATH as picturesPath, fz.MANAGER_CN_NM as responsiblePerson, ve.LIAISON_MAN as liaisonMan, ve.BRIEF_INTRODUCTION as briefIntroduction, VENUES_STATUS as venuesStatus, ve.CREATOR as creator, ve.CREATE_TIME as createTime, ve.LAST_MODIFIER as lastModifier, ve.LAST_MODIFY_TIME as lastModifyTime, ve.RELIGIOUS_SECT as dictCd, ve.LONGITUDE as longitude, ve.LATITUDE as latitudes
from RM_VENUES_INFO ve left join RM_SYS_DICT d on ve.RELIGIOUS_SECT=d.DICT_CD left join rm_venues_manager_info fz on fz.MANAGER_ID=ve.RESPONSIBLE_PERSON and fz.MANAGER_TYPE_CD='01' and fz.VALID_IND='1' where VENUES_STATUS!='02'
AND ve.VENUES_NAME LIKE CONCAT('%',#{venue.venuesName},'%')
AND fz.MANAGER_CN_NM LIKE CONCAT('%',#{venue.responsiblePerson},'%')
AND ve.RELIGIOUS_SECT = #{venue.religiousSect}
AND ve.VENUES_PHONE LIKE CONCAT('%',#{venue.venuesPhone},'%')
AND ve.TOWN = #{venue.town}
AND ve.AREA = #{venue.area}
and ve.VENUES_ID IN
#{value}
order by ve.LAST_MODIFY_TIME desc limit #{page},#{size}

mapper.update:
UPDATE
    RM_VENUES_INFO
set
    LONGITUDE=#{longitude},
    LATITUDE= #{Latitude}
WHERE VENUES_ID = #{venuesId}

mapper.select:
select ve.VENUES_ID as venuesId , ve.LONGITUDE as longitude, ve.LATITUDE as Latitude, d.DICT_CN_DESC as religiousSectCn, ve.RELIGIOUS_SECT as religiousSect, ve.VENUES_NAME as venuesName from RM_VENUES_INFO ve left join RM_SYS_DICT d on ve.RELIGIOUS_SECT=d.DICT_CD where VENUES_STATUS='01' and ve.LONGITUDE is not null and ve.LATITUDE is not null
AND ve.TOWN = #{vo.searchThree}
and ve.RELIGIOUS_SECT in
#{value}
and (ve.RESPONSIBLE_PERSON LIKE concat('%',#{vo.searchOne},'%') or ve.VENUES_ADDRES LIKE concat('%',#{vo.searchOne},'%') or ve.VENUES_NAME LIKE concat('%',#{vo.searchOne},'%') )
AND ve.TOWN = #{vo.town}
AND ve.AREA = #{vo.area}
and ve.VENUES_ID IN
#{value}
order by ve.VENUES_ID desc

select distinct g.IDENTITY as IDENTITY,g.USER_NM,g.USER_MOBILE from rm_sys_user_info g left join rm_sys_user_info u on g.area=u.area where g.VALID_IND='1' and g.IDENTITY in ('10000002','10000003','10000004','10000005')
and u.REL_VENUES_ID = #{relVenuesId}
order by g.IDENTITY
