mapper.select:
select VENUES_ID as venuesId , VENUES_NAME as venuesName, d.DICT_CN_DESC as religiousSect, REGISTER_NBR as registerNbr, VENUES_PHONE as venuesPhone, ORGANIZATION as organization, VENUES_ADDRES as venuesAddres, PICTURES_PATH as picturesPath, RESPONSIBLE_PERSON as responsiblePerson, LIAISON_MAN as liaisonMan, BRIEF_INTRODUCTION as briefIntroduction, VENUES_STATUS as venuesStatus, CREATOR as creator, CREATE_TIME as createTime, LAST_MODIFIER as lastModifier, LAST_MODIFY_TIME as lastModifyTime
from RM_VENUES_INFO ve left join RM_SYS_DICT d on ve.RELIGIOUS_SECT=d.DICT_CD where VENUES_STATUS!='02'
AND VENUES_NAME LIKE CONCAT('%',#{venuesName},'%')
AND RESPONSIBLE_PERSON LIKE CONCAT('%',#{responsiblePerson},'%')
AND RELIGIOUS_SECT = #{religiousSect}
order by LAST_MODIFY_TIME desc limit #{page},#{size}
