mapper.update:
update RM_VENUES_MANAGER_INFO
set VALID_IND='0' ,LAST_MODIFIER=#{lastModifier}, LAST_MODIFY_TIME=#{lastModifyTime}
where MANAGER_ID = #{managerId}

mapper.select:
select MANAGER_ID as managerId, MANAGER_CN_NM as managerCnNm, MANAGER_MOBILE as managerMobile from RM_VENUES_MANAGER_INFO ma where VALID_IND='1'
and MANAGER_CN_NM LIKE CONCAT('%',#{vo.searchOne},'%')
order by ma.LAST_MODIFY_TIME desc limit #{vo.page},#{vo.size}

select

from RM_VENUES_MANAGER_INFO ma
where ma.MANAGER_ID=#{managerId}
