mapper.select:
select
from RM_SYS_MENU_INFO where 1=1
and RESOURCE_TYPE = #{resourceType}

select
from RM_SYS_MENU_INFO where MENU_ID in
#{menuPrtIds}

select MENU_ID as id , MENU_NM as name, RESOURCE_TYPE as type from RM_SYS_MENU_INFO where RESOURCE_TYPE in ('03','04')
and PARENT_ID = #{parentId}
order by SORT_LVL

select MENU_ID as id , MENU_NM as name, RESOURCE_TYPE as type from RM_SYS_MENU_INFO where RESOURCE_TYPE ='05'
and PARENT_ID = #{parentId}
order by SORT_LVL
