mapper.select:
select
from RM_SYS_MENU_INFO where VALID_IND='1'
and RESOURCE_TYPE = #{resourceType}

select
from RM_SYS_MENU_INFO where VALID_IND='1' and MENU_ID in
#{menuPrtIds}

select me.MENU_ID as id , me.MENU_NM as name, me.RESOURCE_TYPE as type from RM_SYS_USER_POST_REL rel left join RM_SYS_MENU_INFO me on rel.POST_CD=me.MENU_ID where me.RESOURCE_TYPE in ('03','04') and me.VALID_IND='1' and rel.USER_ID=#{userId}
and me.PARENT_ID = #{parentId}
order by me.SORT_LVL

select me.MENU_ID as id , me.MENU_NM as name, me.RESOURCE_TYPE as type from RM_SYS_USER_POST_REL rel left join RM_SYS_MENU_INFO me on rel.POST_CD=me.MENU_ID where me.RESOURCE_TYPE ='05' and me.VALID_IND='1' and rel.USER_ID=#{userId}
and me.PARENT_ID = #{parentId}
order by me.SORT_LVL
