mapper.select:
select

from RM_SYS_USER_INFO
where LOGIN_NM = #{loginNm} or USER_NM= #{loginNm} or USER_MOBILE= #{loginNm}

select

from RM_SYS_USER_INFO
where USER_ID = #{userId}

select
    COUNT(USER_ID)
from RM_SYS_USER_INFO where  USER_MOBILE= #{userMobile}

select

from RM_SYS_USER_INFO ve
where VALID_IND='1'

    AND  USER_NM LIKE CONCAT('%',#{vo.searchOne},'%')


    AND  IDENTITY = #{vo.searchTwo}


    AND  ve.TOWN = #{vo.town}


    AND  ve.AREA = #{vo.area}


order by LAST_MODIFY_TIME desc
limit #{vo.page},#{vo.size}

select
    count(USER_ID)
from RM_SYS_USER_INFO ve
where  VALID_IND='1'

    AND  USER_NM LIKE CONCAT('%',#{vo.searchOne},'%')


    AND  IDENTITY = #{vo.searchTwo}


    AND  ve.TOWN = #{vo.town}


    AND  ve.AREA = #{vo.area}


    AND ve.REL_VENUES_ID =#{vo.venues}

select distinct zz.userMobile from( (select zy.USER_NBR as userNbr, zy.USER_MOBILE as userMobile, zy.USER_NM as userNm from rm_sys_user_info zy where zy.VALID_IND='1'
AND zy.REL_VENUES_ID LIKE CONCAT('%',#{relVenuesId},'%')
)union all (select jg.USER_NBR as userNbr, jg.USER_MOBILE as userMobile, jg.USER_NM as userNm from rm_sys_user_info jg left join rm_sys_user_info zy on jg.town=zy.town where jg.VALID_IND='1' and jg.IDENTITY='10000005' and zy.IDENTITY='10000007'
AND zy.REL_VENUES_ID LIKE CONCAT('%',#{relVenuesId},'%')
) ) zz

( select zy.USER_NBR as userNbr, zy.USER_MOBILE as userMobile, zy.USER_NM as userNm from rm_sys_user_info zy where zy.VALID_IND='1'
AND zy.REL_VENUES_ID LIKE CONCAT('%',#{relVenuesId},'%')
) union all ( select distinct al.USER_NBR as userNbr, al.USER_MOBILE as userMobile, al.USER_NM as userNm from rm_sys_user_info al left join rm_sys_user_info zy on al.town=zy.town and zy.IDENTITY='10000007' where al.VALID_IND='1' and al.IDENTITY in ('10000004','10000005')
AND zy.REL_VENUES_ID LIKE CONCAT('%',#{relVenuesId},'%')
) union all ( select distinct al.USER_NBR as userNbr, al.USER_MOBILE as userMobile, al.USER_NM as userNm from rm_sys_user_info al left join rm_sys_user_info zy on al.area=zy.area and zy.IDENTITY='10000007' where al.VALID_IND='1' and al.IDENTITY in ('10000002','10000003')
AND zy.REL_VENUES_ID LIKE CONCAT('%',#{relVenuesId},'%')
)

select jie.USER_NM as userNm, jie.USER_MOBILE as userMobile FROM RM_SYS_USER_INFO zu LEFT JOIN RM_SYS_USER_INFO jie on zu.town=jie.town and jie.IDENTITY in ('10000004','10000005') WHERE jie.VALID_IND='1'
and zu.USER_MOBILE = #{loginNm}
and zu.IDENTITY = #{identity}

select qu.USER_NM as userNm, qu.USER_MOBILE as userMobile FROM RM_SYS_USER_INFO jie LEFT JOIN RM_SYS_USER_INFO qu on qu.area=jie.area and qu.IDENTITY in ('10000002','10000003') WHERE qu.VALID_IND='1'
and jie.USER_MOBILE = #{loginNm}
and jie.IDENTITY = #{identity}

mapper.update:
update RM_SYS_USER_INFO
set
    PASSWORDS = #{passwords},
    WEAK_PW_IND=#{weakPwInd},
    LAST_MODIFY_TIME = #{lastModifyTime}
WHERE USER_ID = #{userId}

mapper.select:
SELECT zz.LOGIN_NM as loginNm,zz.USER_NM as userNm,zz.USER_MOBILE as userMobile FROM rm_sys_user_info zy,rm_sys_user_info zz where zz.REL_VENUES_ID LIKE CONCAT('%',#{relVenuesId},'%') and zz.VALID_IND='1' and zz.IDENTITY='10000006' and zy.IDENTITY='10000007'
and zy.USER_MOBILE=#{userMobile}
and zy.LOGIN_NM=#{loginNm}

SELECT jg.LOGIN_NM as loginNm,jg.USER_NM as userNm,jg.USER_MOBILE as userMobile FROM rm_sys_user_info zz,rm_sys_user_info jg where zz.town=jg.town and jg.VALID_IND='1' and jg.IDENTITY='10000005' and zz.IDENTITY='10000006'
and zz.USER_MOBILE=#{userMobile}
and zz.LOGIN_NM=#{loginNm}

SELECT jw.LOGIN_NM as loginNm,jw.USER_NM as userNm,jw.USER_MOBILE as userMobile FROM rm_sys_user_info jw,rm_sys_user_info jg where jw.town=jg.town and jw.VALID_IND='1' and jw.IDENTITY='10000004' and jg.IDENTITY='10000005'
and jg.USER_MOBILE=#{userMobile}
and jg.LOGIN_NM=#{loginNm}

SELECT qg.LOGIN_NM as loginNm,qg.USER_NM as userNm,qg.USER_MOBILE as userMobile FROM rm_sys_user_info jw,rm_sys_user_info qg where qg.area=jw.area and qg.VALID_IND='1' and qg.IDENTITY='10000003' and jw.IDENTITY='10000004'
and jw.USER_MOBILE=#{userMobile}
and jw.LOGIN_NM=#{loginNm}

SELECT qw.LOGIN_NM as loginNm,qw.USER_NM as userNm,qw.USER_MOBILE as userMobile FROM rm_sys_user_info qw,rm_sys_user_info qg where qg.area=qw.area and qw.VALID_IND='1' and qw.IDENTITY='10000002' and qg.IDENTITY='10000003'
and qg.USER_MOBILE=#{userMobile}
and qg.LOGIN_NM=#{loginNm}
